import java.util.Scanner;public class CarPark {    private int totalRegularParks;    private int totalMotorcycleParks;    private int totalParks;    private int availableRegularParks;    private int availableMotorcycleParks;    public int getTotalMotorcycleParks() {        return totalMotorcycleParks;    }    public void setTotalMotorcycleParks(int totalMotorcycleParks) {        this.totalMotorcycleParks = totalMotorcycleParks;    }    public int getTotalParks() {        return totalParks;    }    public void setTotalParks(int totalParks) {        this.totalParks = totalParks;    }    public int getAvailableRegularParks() {        return availableRegularParks;    }    public void setAvailableRegularParks(int availableRegularParks) {        this.availableRegularParks = availableRegularParks;    }    public int getAvailableMotorcycleParks() {        return availableMotorcycleParks;    }    public void setAvailableMotorcycleParks(int availableMotorcycleParks) {        this.availableMotorcycleParks = availableMotorcycleParks;    }    public CarPark(int regularParks, int totalMotorcycleParks) {        this.totalRegularParks = regularParks;        this.totalMotorcycleParks = totalMotorcycleParks;        this.totalParks = regularParks+ totalMotorcycleParks;        this.availableRegularParks = (int) (Math.random()* totalRegularParks);        this.availableMotorcycleParks = (int) (Math.random()* totalMotorcycleParks);    }    public boolean hasCarParksAvailable() {        return availableRegularParks > 0;    }    public boolean hasVanParksAvailable() {        return availableRegularParks > 2;    }    public boolean hasMotorCycleParksAvailable() {        return availableMotorcycleParks > 0 || availableRegularParks > 0;    }    public int getTotalRegularParks() {        return totalRegularParks;    }    public void setTotalRegularParks(int totalRegularParks) {        this.totalRegularParks = totalRegularParks;    }    public String printCarParkDetails(){        return "There are " + availableRegularParks + " car parks available and "+ availableMotorcycleParks + " motorcycle parks available\n";    }    public boolean printGoodbye(Scanner scanner){        System.out.println("Thank you for visiting the car park!\nIs there anything else we can help with today?\n1.  Yes\n2.  No");        boolean isActive = true;        int userInput = scanner.nextInt();        while(isActive){            if(userInput == 1 || userInput == 2){                isActive = false;            } else {                System.out.println("Please enter either 1 or 2");                userInput = scanner.nextInt();            }        }        return userInput == 1;    }}