public class MotorCycle extends Vehicle{    public MotorCycle() {        super(1, "motorcycle");    }    @Override    public void park(CarPark carpark) {        if(carpark.hasMotorCycleParksAvailable() ){            carpark.setAvailableMotorcycleParks(carpark.getAvailableMotorcycleParks()-requiredParks);            System.out.println("You have successfully parked your motorcycle!");        } else if (carpark.getAvailableRegularParks() > 0 ){            carpark.setAvailableRegularParks(carpark.getAvailableRegularParks()-requiredParks);            System.out.println("You have successfully parked your motorcycle!");        }        System.out.println(carpark.printCarParkDetails());    }    @Override    public void printVehicleDetails() {    }}