public class Van extends Vehicle{    public Van() {        super(3, "van");    }    @Override    public void park(CarPark carpark) {        if(carpark.getAvailableRegularParks() > 2 ){            carpark.setAvailableRegularParks(carpark.getAvailableRegularParks()-requiredParks);            System.out.println("You have successfully parked your " + type);        } else {            System.out.println("There are not enough spaces for you to park! Please try elsewhere!");        }        System.out.println(carpark.printCarParkDetails());    }    @Override    public void printVehicleDetails() {    }}